let extractedText = '';

for (const item of items) {
  if (item.json.text && item.json.text.trim() !== '') {
    extractedText = item.json.text.trim();
    break;
  }
  if (item.json.Texto && item.json.Texto.trim() !== '') {
    extractedText = item.json.Texto.trim();
    break;
  }
}

return [{ json: { text: extractedText } }];

const producto = ($json["nombre_producto"] || "").toLowerCase().replace(".jpg", "").trim();

const chatId = $json["to"] || $('WHATSAPP').item.json.body.messages[0].chat_id;

let urlImagen = "";

if (producto.includes("gotas")) {
  urlImagen = "https://i.ibb.co/5g1Tztny/gotas-ansiedad.jpg";
} else if (producto.includes("crema")) {
  urlImagen = "https://i.ibb.co/5WXZHfRT/crema-antiarrugas.jpg";
} else {
  urlImagen = "https://i.ibb.co/5WXZHfRT/crema-antiarrugas.jpg"; // fallback
}

return [{
  json: {
    to: chatId,
    media: urlImagen,
    productoRecibido: producto
  }
}];
