const items = [];
for (const item of $input.all()) { // Itera sobre cada √≠tem de entrada al nodo Code
  let aiResponseText = "Lo siento, hubo un error interno (no se pudo acceder a la respuesta de la IA)."; // Mensaje de fallback

  try {
      let rawText = null;

      // *** ACCESO SIMPLIFICADO Y DIRECTO A LA SALIDA DEL AI AGENT ***
      // Asume que la salida del AI Agent es el 'item' que llega directamente a este nodo 'Code'.
      // Intentar obtener el texto de la IA buscando en item.json.output o item.json.text
      if (item.json) { // Asegurarse de que el item tenga una propiedad 'json'
          if (typeof item.json.output === 'string') {
              rawText = item.json.output;
          } else if (typeof item.json.text === 'string') { 
              rawText = item.json.text;
          }
      }

      // Si rawText sigue siendo null aqu√≠, es porque no se encontr√≥ en item.json.
      // En este caso, el AI Agent no est√° proporcionando su output como item.json.output/text.
      // Esta es la rama que toma si el AI Agent no es el nodo inmediatamente anterior, o si su output no es el esperado.
      if (!rawText) {
          // Intentar obtenerlo a trav√©s de $node.getNode() como √∫ltimo recurso,
          // aunque el error "Referenced node doesn't exist" sugiere que esto puede fallar.
          try {
              const aiAgentNode = $node.getNode('AI Agent'); 
              if (aiAgentNode && aiAgentNode.json) {
                  if (typeof aiAgentNode.json.output === 'string') {
                      rawText = aiAgentNode.json.output;
                  } else if (typeof aiAgentNode.json.text === 'string') {
                      rawText = aiAgentNode.json.text;
                  }
              } else if (aiAgentNode && typeof aiAgentNode.output === 'string') { // Para casos donde el output no est√° en .json
                   rawText = aiAgentNode.output;
              } else if (aiAgentNode && typeof aiAgentNode.text === 'string') { // Para casos donde el output no est√° en .json
                   rawText = aiAgentNode.text;
              }
          } catch (getNodeError) {
              console.warn("Fallo al obtener AI Agent via $node.getNode():", getNodeError.message || getNodeError);
          }
      }


      if (rawText) {
          // Limpieza de barras invertidas (del problema anterior de \"asistente\")


          // Limpieza de caracteres de control ilegales para JSON
           //cleanedText = rawText.replace(/[\u0000-\u0008\u000B\u000C\u000E-\u001F\u007F-\u009F]/g, ''); 

          // Eliminaci√≥n A√öN M√ÅS AGRESIVA de saltos de l√≠nea y espacios especiales (causa de truncamiento)
         let cleanedText = rawText.replace(/[\n\r\t\f\v\u0085\u2028\u2029\u00A0\u1680\u2000-\u200A\u202F\u205F\u3000\""]+/g, ' '); 

          // Normalizar espacios y eliminar espacios al inicio/final
         // cleanedText = cleanedText.replace(/\s+/g, ' ').trim(); 

          // Opcional: Eliminar comillas dobles al inicio y al final si la IA las pone literalmente.
          if (cleanedText.startsWith('"') && cleanedText.endsWith('"')) {
              cleanedText = cleanedText.substring(1, cleanedText.length - 1);
          }

          aiResponseText = cleanedText;

      } else {
          // Este warning se activar√° si rawText sigue siendo null despu√©s de todos los intentos
          console.warn("No se pudo obtener una respuesta de texto v√°lida del AI Agent (valor nulo o no string en ning√∫n formato esperado). Item:", item);
          aiResponseText = "La respuesta de la IA no fue v√°lida (formato inesperado de la IA).";
      }

  } catch (e) {
      // Este catch capturar√° si hay un error inesperado fuera de las comprobaciones de acceso
      console.error("Error inesperado en el nodo Code (posible error de JavaScript en el c√≥digo):", e);
      aiResponseText = `ERROR EN CHATBOT (n8n): ${e.message || e}`; 
  }

  const responseBody = {
    response: aiResponseText
  };

  items.push({ json: responseBody });
}
return items;
