// Limpiar el texto de entrada del usuario
let producto = $input.first().json.Producto;

function limpiarTexto(texto) {
  return texto
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/√±/g, 'n')
    .replace(/√ë/g, 'N')
    .replace(/\s+/g, '+');
}

let productoLimpio = limpiarTexto(producto);

return [{
  json: {
    producto: productoLimpio,
    productoOriginal: producto,
    timestamp: new Date().toISOString()
  }
}];

